import{_ as l,c as e,ah as i,o as a}from"./chunks/framework.BjDJcZmr.js";const p=JSON.parse('{"title":"Neumorphism 设计优化","description":"总结 Neumorphism UI 设计风格。","frontmatter":{"date":"2025-07-15T00:00:00.000Z","title":"Neumorphism 设计优化","category":"Charlotte","tags":["CSS"],"description":"总结 Neumorphism UI 设计风格。"},"headers":[],"relativePath":"posts/neumorphism-ui.md","filePath":"posts/neumorphism-ui.md"}'),r={name:"posts/neumorphism-ui.md"};function t(s,o,d,c,n,u){return a(),e("div",null,o[0]||(o[0]=[i('<h1 id="neumorphism" tabindex="-1">Neumorphism <a class="header-anchor" href="#neumorphism" aria-label="Permalink to &quot;Neumorphism&quot;">​</a></h1><details class="details custom-block"><summary>目录</summary><nav class="table-of-contents"><ul><li><a href="#整体风格">整体风格</a></li><li><a href="#ui-元素风格">UI 元素风格</a></li><li><a href="#字体">字体</a></li><li><a href="#交互效果">交互效果</a></li><li><a href="#图标和装饰">图标和装饰</a></li><li><a href="#布局">布局</a></li><li><a href="#响应式设计">响应式设计</a></li><li><a href="#颜色和视觉平衡">颜色和视觉平衡</a></li><li><a href="#细节和品质感">细节和品质感</a></li></ul></nav></details><h2 id="整体风格" tabindex="-1">整体风格 <a class="header-anchor" href="#整体风格" aria-label="Permalink to &quot;整体风格&quot;">​</a></h2><p><strong>新拟态风格 (Neumorphism)</strong>：通过柔和的阴影、低对比度色彩和嵌入/浮动效果，模拟物理世界的立体感</p><ul><li><p><strong>阴影</strong>：内外双重 <code>box-shadow</code> 实现浮起或嵌入效果</p></li><li><p><strong>背景色</strong>：</p><ul><li>使用浅色调 (如：米白色 <code>#F5F5F5</code>、淡蓝灰 <code>#E8ECF2</code>)，避免高对比度，提升温和清新感</li><li>遵守 <strong>&quot;60-30-10&quot; 色彩法则</strong>，使视觉效果更具层次感 <ul><li><strong>60% 主色调</strong>：米白色 (<code>#F5F5F5</code>)</li><li><strong>30% 次要色</strong>：淡蓝灰 (<code>#E8ECF2</code>)</li><li><strong>10% 点缀色</strong>：高级灰 (<code>#B8C6D9</code>)</li></ul></li><li><strong>渐变色</strong>：按钮等交互元素使用渐变 (如：<code>linear-gradient(135deg, #E8ECF2, #F5F5F5)</code>) 提升视觉吸引力</li></ul></li></ul><h2 id="ui-元素风格" tabindex="-1">UI 元素风格 <a class="header-anchor" href="#ui-元素风格" aria-label="Permalink to &quot;UI 元素风格&quot;">​</a></h2><p>主要 UI 控件 (卡片、按钮、输入框等)，采用圆角和三级阴影设计，结合黄金分割比例和交互动画</p><ul><li><strong>圆角</strong>：<code>12-16px</code>，双重阴影效果，模拟浮动/按下质感</li><li><strong>阴影</strong>： <ul><li>一级阴影 (轻微浮动)：<code>8px</code> 模糊度</li><li>二级阴影 (中度浮动)：<code>12px</code> 模糊度</li><li>三级阴影 (强烈浮动)：<code>16px</code> 模糊度</li></ul></li><li><strong>黄金分割比例</strong>：卡片宽高和间距时使用黄金分割比例 1:1.618</li><li><strong>交互性</strong><ul><li>悬停：阴影增大，轻微上浮 (<code>0.2s</code>)</li><li>点击：内嵌阴影，快速下沉 (<code>0.1s</code>)</li><li>释放：缓慢回弹 (<code>0.3s</code>)</li><li>使用 <code>ease-out</code> 确保平滑过渡，动画效果平滑过渡，保证视觉效果的流畅性和自然感</li></ul></li></ul><h2 id="字体" tabindex="-1">字体 <a class="header-anchor" href="#字体" aria-label="Permalink to &quot;字体&quot;">​</a></h2><ul><li><strong>字体选择</strong>：简洁无衬线字体 (如：<code>Roboto</code>、<code>Inter</code>、<code>Open Sans</code>)，确保良好的可读性和现代感，</li><li><strong>字体颜色</strong>：浅灰色或低对比黑色 (如：<code>#B8C6D9</code>)，标题加粗，正文较轻</li><li><strong>字体大小</strong><ul><li>大标题：<code>24px</code>，粗体</li><li>副标题：<code>20px</code>，半粗体</li><li>正文：<code>16px</code>，常规</li><li>说明文字：<code>14px</code>，细体</li></ul></li></ul><h2 id="交互效果" tabindex="-1">交互效果 <a class="header-anchor" href="#交互效果" aria-label="Permalink to &quot;交互效果&quot;">​</a></h2><ul><li><strong>按钮悬停</strong>：阴影增大，呈现浮动感</li><li><strong>点击反馈</strong>：内嵌阴影，模拟按下效果</li><li><strong>平滑过渡</strong>：使用 <code>ease-out</code> 使按钮、阴影、大小等变化有 &quot;缓出缓入&quot; 功效</li><li><strong>三段式动画</strong>：悬停 (<code>0.2s</code> 上浮)、点击 (<code>0.1s</code> 下沉)、释放 (<code>0.3s</code> 回弹)</li></ul><h2 id="图标和装饰" tabindex="-1">图标和装饰 <a class="header-anchor" href="#图标和装饰" aria-label="Permalink to &quot;图标和装饰&quot;">​</a></h2><ul><li><strong>图标设计</strong>：简洁线条，避免复杂细节，与新拟态风格一致</li><li><strong>阴影</strong>：微弱阴影效果，增强浮动感，提升层次感</li><li><strong>渐变</strong>：微小渐变 (如：<code>#E8ECF2</code> 到 <code>#F5F5F5</code>) 使图标更加灵动和细腻</li></ul><h2 id="布局" tabindex="-1">布局 <a class="header-anchor" href="#布局" aria-label="Permalink to &quot;布局&quot;">​</a></h2><ul><li><p><strong>间距</strong></p><ul><li>使用 <code>8px</code> 倍数 (<code>8px</code>、<code>16px</code>、<code>24px</code>、<code>32px</code>)，确保整洁</li><li>确保 UI 组件有足够的 &quot;呼吸空间&quot;，避免界面过于拥挤，提升用户体验</li></ul></li><li><p><strong>网格布局</strong>：使用模块化栅格系统，保持清晰整齐的排版风格，支持响应式设计，适配不同的屏幕尺寸</p><ul><li>桌面端：<code>12</code> 列栅格</li><li>平板端：<code>8</code> 列栅格</li><li>移动端：<code>4</code> 列栅格</li></ul></li></ul><h2 id="响应式设计" tabindex="-1">响应式设计 <a class="header-anchor" href="#响应式设计" aria-label="Permalink to &quot;响应式设计&quot;">​</a></h2><ul><li>桌面与移动端适配，移动端垂直布局</li><li>动态调整元素显示，确保一致性和可操作性</li></ul><h2 id="颜色和视觉平衡" tabindex="-1">颜色和视觉平衡 <a class="header-anchor" href="#颜色和视觉平衡" aria-label="Permalink to &quot;颜色和视觉平衡&quot;">​</a></h2><ul><li><p><strong>低饱和度中性色</strong>：避免鲜艳色，保持温和感</p></li><li><p><strong>阴影对比</strong>：阴影与背景轻微对比，增强浮动感</p></li><li><p><strong>纹理背景</strong>：透明度 <code>5%-10%</code> 的渐变或线性图案，增加精致感</p></li></ul><h2 id="细节和品质感" tabindex="-1">细节和品质感 <a class="header-anchor" href="#细节和品质感" aria-label="Permalink to &quot;细节和品质感&quot;">​</a></h2><ul><li><p><strong>精致设计</strong>：柔和线条，避免粗糙</p></li><li><p><strong>边框</strong>：<code>0.5px</code> 边框，颜色稍深 (如：<code>#E8ECF2</code>)</p></li><li><p><strong>圆角差异化</strong></p><ul><li><p>主要容器：<code>16px</code></p></li><li><p>次要容器：<code>12px</code></p></li><li><p>按钮元素：<code>8px</code></p></li></ul></li></ul>',22)]))}const g=l(r,[["render",t]]);export{p as __pageData,g as default};
